<?php/** * Created by PhpStorm. * User: user * Date: 04.06.18 * Time: 05:37 */require_once('Simpla.php');class Wall extends Simpla{    public function get_wall($filter)    {        if (isset($filter['user_id']))            $user_id = $this->db->placehold('AND w.user_id=?', $filter['user_id']);        $query = "SELECT *				FROM __wall AS w				WHERE 1					$user_id ORDER BY w.date DESC";        $this->db->query($query);        return $this->db->results('count');    }    public function add_wall($wall)    {        if ($this->db->query("INSERT INTO __wall SET ?%", $wall))        {            $id = $this->db->insert_id();            /*$this->db->query("UPDATE __products SET position=id WHERE id=?", $id);*/            return $id;        } else            return false;    }}